const UserForms = props => {
  const [animal, setAnimal] = useState([]);
  useEffect(() => {
    if (props.status) {
      setAnimal([...animal, props.status]);
    }
    [animal];
  });

  // const handleChange = event => {
  //   setAnimal({ ...animal, [event.target.name]: event.target.value });
  // };

  // const handleSubmit = event => {
  //   event.preventDefault();
  //   console.log(animal.species);
  // };

  //<Field /> subs for input

  //Forms takes care of value and onChange key/values
  // value={props.values.species}
  // onChange={props.handleChange}

  return (
    <div className="animal-form">
      {/* <form onSubmit={event => handleSubmit(event)}> */}
      <Form>
        Species:
        <Field type="text" name="species" placeholder="species" />
        {props.touched.species && props.errors.species && (
          <p className="error">{props.error.species}</p>
        )}
        <Field type="text" name="size" placeholder="size" />
        {props.touched.size && props.errors.size && (
          <p className="error">{props.error.size}</p>
        )}
        <Field component="select" name="diet" className="food-select">
          <option>Please Submit</option>
          <option value="herbivore">herbivore</option>
          <option value="carnivore">carnivore</option>
          <option value="omnivore">omnivore</option>
        </Field>
        <label className="checkbox-container">
          <Field
            type="checkbox"
            name="vaccinations"
            checked={props.values.vaccinations}
          />
          vaccinations
          <span className="checkmark" />
        </label>
        <Field
          component="textarea"
          type="text"
          name="notes"
          placeholder="notes"
        />
        <button type="sumbit">Submit!</button>
        {/* </form> */}
      </Form>
      {animal.map(animal => {
        <ul key={animal.id}>
          <li>Species: {animal.species}</li>
          <li>size: {animal.size}</li>
          <li>Diet: {animal.diet}</li>
        </ul>;
      })}
    </div>
  );
};

const myMapPropsToValues = props => {
  const returnObj = {
    species: props.species || "", //must match value={props.values. SPECIES} from input^
    size: props.size || "",
    diet: props.diet || "",
    vaccinations: props.vaccinations || true,
    notes: props.notes || ""
  };
  return returnObj;
};
//works with <Form /> so you dont need the above handleSubmit function ^^

// Axios call from the server
const myHandleSubmit = (values, { setStatus }) => {
  console.log("sumbit pressed ...sending...");
  Axios.post("https://reqres.in/api/users/", values)
    .then(res => {
      console.log(res);
      setStatus(res.data);
    })
    .catch(err => console.log(err));
};

const yupSchema = Yup.object().shape({
  species: Yup.string().required("please type a species"),
  size: Yup.string().required("please type a size")
});

const formikObj = {
  mapPropsToValues: myMapPropsToValues,
  handleSubmit: myHandleSubmit,
  validationSchema: yupSchema
};

const EnhancedFormHOC = withFormik(formikObj);
// the component being passed must already be declared ie.(UserForms)
const FormikEnhancedAnimalForm = EnhancedFormHOC(UserForms);

export default FormikEnhancedAnimalForm;
